'abbott-shared/util/ScrollTo':function(){
/**
 * This module allows for easy scrolling to top of an element, while accounting for the fact that the fixed header
 * is not included in the document flow (so the scroll needs to be adjusted for that).
 */
define(['abbott-shared/util/responsiveUtil'],
    function(responsiveUtil) {
        var HEADER_DRAWER_SELECTOR = '.siteNav-drawer-wrapper';
        var TITLE_BAR_SELECTOR = '.titleBar';

        var ScrollTo = {
            /**
             * Scrolls to the given element offset, and adjusting for the header.
             *
             * @param $element The element to scroll to the top of. {@type jQuery}
             */
            topOfElement: function ($element) {

                $('html, body').scrollTop(this.calculateVerticalScrollbarPositionInPixelsForElement($element));
            },

            /**
             * Same as topOfElement except animates as a slide (instead of abrupt anchor) to location on page so
             * to of element falls under the fixed header.
             * 
             * @param $element {@type jQuery} the element to scroll to top of
             */
            animateToTopOfElement: function($element, callback) {

                var verticalScrollbarPosition = this.calculateVerticalScrollbarPositionInPixelsForElement($element);

                $('html, body').animate({
                    scrollTop: verticalScrollbarPosition
                }, callback);
            },

            /**
             * Calculates the vertical scrollbar position in pixels so passed element can be anchored to top
             * of page under fixed header.
             *
             * @param $element the element to calculate
             * @returns {number} the number of pixels
             */
            calculateVerticalScrollbarPositionInPixelsForElement: function ($element) {

                var elementOffset = $element.offset().top;
                var $header = $('header');
                var $headerDrawer = $header.find(HEADER_DRAWER_SELECTOR);

                // Get the height of the secondary navigation drawer if exists
                var headerDrawerHeight = $headerDrawer.length ? $headerDrawer.height() : 0;

                // Secondary nav drawer should not be included in height calculation offset so we subtract it
                var headerHeightOffset = $header.height() - headerDrawerHeight;

                // We need to explicitly set the header offset to be the mobile header height and not include
                // the secondary nav and division title bar
                if (!responsiveUtil.breakTabletLandscape()) {
                    headerHeightOffset = $(TITLE_BAR_SELECTOR, $header).outerHeight(true);
                }

                return elementOffset - headerHeightOffset;
            }
        };
        
        return ScrollTo;
    });
},
