'pepper/utilities/pageState':function(){
define([
    'module', 'vodori/provide', 'pepper/enums/editingEnum', 'dojo/dom-class', 'dojo/has',
    'dojo/topic', 'pepper/events'

], function (module, provide, editingEnum, domClass, has, topic, events) {
    /**
     * Provides methods for getting and setting the Edit Mode state of an open page
     * Encapsulates all of the DOM class checking and the event publishing
     */
    var docElement = document.documentElement;

    return provide(module.id, {

        isReadyToEdit: function () {
            return domClass.contains(docElement, editingEnum.READY_TO_EDIT_CLASS);
        },

        isPossibleToEdit: function () {
            return domClass.contains(docElement, editingEnum.POSSIBLE_TO_EDIT_CLASS);
        },

        inAuthorMode: function () {
            return domClass.contains(docElement, editingEnum.AUTHOR_MODE);
        },

        setReadyToEdit: function () {
            domClass.add(docElement, editingEnum.READY_TO_EDIT_CLASS);

            // this is initialized on first page load for availability to the live site,
            // but to maintain accuracy we update it whenever the page state changes.
            has.add('edit-mode', true, true, true);

            this.requestStateUpdate();
        },

        clearReadyToEdit: function () {
            domClass.remove(docElement, editingEnum.READY_TO_EDIT_CLASS);

            // this is initialized on first page load for availability to the live site,
            // but to maintain accuracy we update it whenever the page state changes.
            has.add('edit-mode', false, true, true);

            this.requestStateUpdate();
        },

        setPossibleToEdit: function () {
            // this is initialized on first page load for availability to the live site,
            // but to maintain accuracy we update it whenever the page state changes.
            has.add('can-edit', true, true, true);

            domClass.add(docElement, editingEnum.POSSIBLE_TO_EDIT_CLASS);
        },

        clearPossibleToEdit: function () {
            // this is initialized on first page load for availability to the live site,
            // but to maintain accuracy we update it whenever the page state changes.
            has.add('can-edit', false, true, true);

            domClass.remove(docElement, editingEnum.POSSIBLE_TO_EDIT_CLASS);
        },

        requestStateUpdate: function () {
            var state = this.isReadyToEdit() ? 'edit' : 'preview';
            topic.publish(events.page.state.changed, state);
        }

        /*
         * Currently, there is no need to clearPossible, in client code
         */
    });
});

},
