'vodori/util/Enum':function(){
/**
 * Make enumerators in JavaScript. based on: http://goo.gl/L9hqf
 */

define(['lodash', 'module'], function (_, module) {
    /**
     * @name vodori.util.Enum
     * @class
     * @constructor
     * @example
     * Build an enumerator. There are a few ways to do it:
     *
     * integer based:
     *     var MyEnum = new Enum('BANANA', 'ORANGE', 'APPLE');
     *     // Use like
     *     someFruit === MyEnum.BANANA
     *
     * string based with Arrays:
     *   var MyEnum = new Enum(
     *       ['BANANA_EVENT', '/foo/bar/baz/randomstuff'],
     *       ['ORANGE_EVENT', '/foo/bar/baz/otherrandomstuff']
     *   );
     *   // Use like
     *   someFruitEventName === MyEnum.BANANA_EVENT
     *
     * string based with Objects
     *
     *   var MyEnum = new Enum(
     *       {'BANANA_EVENT': '/foo/bar/baz/randomstuff'},
     *       {'ORANGE_EVENT': '/foo/bar/baz/otherrandomstuff'}
     *   );
     *   // Use like
     *   someFruitEventName === MyEnum.BANANA_EVENT
     *
     * @return {Enum}
     */
    function Enum() {
        var args = [].slice.call(arguments);
        _.forEach(args, function (arg, i) {
            // JS doesn't really care if it's a number
            if (_.isString(arg) || _.isNumber(arg)) {
                this[String(arg)] = i;
            } else if (_.isArray(arg)) {
                // pop's usually a bit quicker, but not enough to bother caching.
                this[arg.shift()] = arg.pop();
            } else if (_.isObject(arg)) {
                for (var key in arg) {
                    this[key] = arg[key];
                }
            } else {
                throw 'Invalid argument in ' + module.id + 'constructor';
            }
        }, this);

        return this;
    }

    /**
     * See if any of the values in this Enum are the same as prop. Note: this
     * won't do deep object comparisons.
     *
     * @param {*} prop
     * @return {boolean}
     */
    Enum.prototype.has = function (prop) {
        for (var key in this) {
            if (this.hasOwnProperty(key) && this[key] === prop) {
                return true;
            }
        }
        return false;
    };

    /**
     * Get all available values.
     *
     * @return {Array.<*>}
     */
    Enum.prototype.all = function () {
        var values = [];
        for (var key in this) {
            if (this.hasOwnProperty(key)) {
                values.push(this[key]);
            }
        }
        return values;
    };

    /**
     * Get the key for a given value.
     *
     * @param {*} value
     * @return {string}
     */
    Enum.prototype.key = function (value) {
        for (var key in this) {
            if (this.hasOwnProperty(key) && this[key] === value) {
                return key;
            }
        }
        return null;
    };

    return Enum;
});
},
