'abbott-shared/util/OnLoadVideoUtil':function(){
define([
    'dojo/ready',
    'dijit/registry',
    'dojo/topic',
    'abbott-shared/util/events',
    'abbott-shared/util/HrefUtil',
    'abbott-shared/util/browserDetectionUtil',
    'dojo/_base/lang',
    'pepper/utilities/pageState'
], function(ready,
            registry,
            topic,
            events,
            HrefUtil,
            browserDetectionUtil,
            lang,
            pageState) {
    var OnLoadVideoUtil = {

        /**
         * The video filename in the url query if it exists
         *
         * @type {String}
         */
        queryVideoFilename: null,

        /**
         * Boolean flag to keep track whether an on load video has been found.
         * This is to prevent multiple videos with the same filename from conflicting.
         * This also means that only the first video with the matched filename will play
         *
         * @type {boolean}
         */
        isOnLoadVideoFound: false,

        /**
         * Main initialization function
         */
        init: function() {
            this._parseQueryVideoFilename();

            // Only initialize if there is an on load video query and page is not in edit mode
            if (this.queryVideoFilename && this.queryVideoFilename.length > 0 &&
                !pageState.isReadyToEdit()) {
                this._initVideoPlayerWidgetLoadedListener();
            }
        },

        /**
         * Parses the url for a on load video query
         *
         * @private
         */
        _parseQueryVideoFilename: function() {
			
          	// Note: If the on load video query parameter ever changes, "video" must be updated to the new parameter.
          	// 		 This change must also be applied to GeolocationServiceImpl.java in the isGeolocationRequiredForRequest method
            this.queryVideoFilename = HrefUtil.getQueryParameter("video", window.location.href);

            // Trim the filename if not null
            this.queryVideoFilename = this.queryVideoFilename ? this.queryVideoFilename.trim() : this.queryVideoFilename;
        },


        /**
         * Initializes the video player widget load listener
         *
         * @private
         */
        _initVideoPlayerWidgetLoadedListener: function() {
            topic.subscribe(events.videoPlayer.widgetLoaded, lang.hitch(this, function(data) {
                if (!this.isOnLoadVideoFound) {
                    this._videoPlayerWidgetLoadedHandler(data);
                }
            }));
        },

        /**
         * The video player widget load handler
         *
         * When a video player widget instantiates, if the filename matches the query filename,
         * open and play the video
         *
         * @param data - The data passed from the emitted widget load event
         * @private
         */
        _videoPlayerWidgetLoadedHandler: function(data) {

            // The emitted event must pass the widget
            if (!data.widget) {
                return;
            }

            var videoPlayerWidget = data.widget;

            // Split the widget's video href by '/' in order to grab the video filename string
            var splitWidgetVideoHref = videoPlayerWidget.videoHref ? videoPlayerWidget.videoHref.split("/") : "";

            // Grab the video filename string
            var widgetVideoFilename = splitWidgetVideoHref[splitWidgetVideoHref.length-1];

            // Only propagate to on load video logic if loaded widget filename exists (should always have a filename)
            if (widgetVideoFilename && widgetVideoFilename.length > 0) {

                // If the query video filename matches the widget video filename, open and play the video
                if (this.queryVideoFilename === widgetVideoFilename) {
                    ready(lang.hitch(this, function() {
                        videoPlayerWidget.renderVideoAndOpenInModal().then(function() {

                            if (!videoPlayerWidget.isNativeHtml5Video) { // videojs
                                videoPlayerWidget.videoJsVideo.play();
                            } else { // html5 video player
                                videoPlayerWidget.html5Video.play();
                            }

                            // Force a redraw in Firefox by hiding/showing body since it has
                            // issues rendering due to hardware acceleration setting
                            if (browserDetectionUtil.isFirefox()) {
                                $('body').hide().show(0);
                            }
                        });
                    }));
                    this.isOnLoadVideoFound = true;
                }
            }
        }
    };

    return OnLoadVideoUtil;
});
},
'abbott-shared/util/HrefUtil':function(){
/*jshint dojo:true devel:true strict:false*/
/*global define:true */
/*
 * Copyright (c) 2015. Vodori, Inc.  All rights reserved.
 *
 * This software is not open source and license for its use and
 * modification can only be granted through an agreement with
 * Vodori, Inc.
 *
 * http://www.vodori.com
 */
define([],
    function() {

        /**
         * Utility for href manipulation
         */
        var HrefUtil = {

            /**
             * Retrieves the query parameter value from the provided href
             * If provided href is null, this will grab from the current href
             *
             * Provided from:
             * http://stackoverflow.com/questions/901115/how-can-i-get-query-string-values-in-javascript
             *
             * @param key - the query key
             * @param href - the href to parse the parameter from
             */
            getQueryParameter: function(key, href) {
                if (!href) {
                    href = window.location.href;
                }
                key = key.replace(/[\[\]]/g, "\\$&");
                var regex = new RegExp("[?&]" + key + "(=([^&#]*)|&|#|$)"),
                    results = regex.exec(href);
                if (!results) { return null; }
                if (!results[2]) { return ''; }
                return decodeURIComponent(results[2].replace(/\+/g, " "));
            },

            /**
             * Gets the filename from a path (not including any query params)
             *
             * Example: /us/en/video.mp4?something=true ==> video.mp4
             *
             * @param href relative or absolute path to a resource you'd like to get the name of
             * @return {string} the filename from a path (not including any query params)
             */
            getFileName: function(href) {
                return href ? href.split('/').pop().split('#')[0].split('?')[0] : "";
            },

            /**
             * Gets the current full page URL with no query params
             *
             * @return {string} the current full page URL with no query params
             */
            getCurrentUrlNoQueryParams: function() {
                return [location.protocol, '//', location.host, location.pathname].join('');
            }
        };

        return HrefUtil;
    }
);
},
