                    domAttr.set(this.imagePreview, { src: this.imgSrc, style: 'cursor: pointer;' });

                    if (this.imgWidth) {
                        domAttr.set(this.imagePreview, 'width', this.imgWidth);
                    }
                    if (this.imgHeight) {
                        domAttr.set(this.imagePreview, 'height', this.imgHeight);
                    }

