'abbott-shared/util/LocalForageUtil':function(){
/**
 * Util for creating and managing LocalForage instances.
 *
 * https://github.com/localForage/localForage
 */
define([
    'vodori/provide',
    'module'
], function (provide, module) {
    return provide(module.id, {

        /**
         * Initialize a LocalForage store with the given store name. By default this creates an IndexedDB store.
         *
         * If IndexedDB is not supported by the browser, it will cascade to WebSQL and then to LocalStorage. Our current
         * browser matrix should support IndexedDB fully.
         *
         * This will either create the store or will retrieve an existing store with the same name. Store names are unique,
         * so no two identically named stores can exist.
         */
        initStore: function (storeName) {
           return localforage.createInstance({
                name: storeName,
                storeName: storeName
            });
        }
    });
});
},
