'abbott-shared/ui/productFilter/GridListToggle':function(){
define([
    'module',
    'vodori/provide',
    'dijit/_WidgetBase',
    'dojo/topic', 'abbott-shared/util/events',
    'vodori/renderTemplate',
    'dojo/string',
    'dojo/text!./template/GridListToggle.html',
    'abbott-shared/ui/productFilter/ViewMode'
], function(
    module,
    provide,
    _WidgetBase,
    topic, events,
    renderTemplate,
    dojoString,
    gridListToggleTemplate,
    ViewMode
) {

    var GRID_TOGGLE_ICON_SELECTOR = '.js--grid-toggle-icon';
    var SHOW_ALL_PRODUCTS_SELECTOR = '.js--show-all-products';
    var NUMBER_OF_RESULTS_SHOWN_SELECTOR = '.js--number-of-results';

    /**
     * This widget handles toggling between grid and list views for the deluxe product filter.
     */
    return provide(module.id, [_WidgetBase], {

        /**
         * This widget's root DOM node
         * {@type jQuery}
         */
        $domNode: null,

        /**
         * The grid/list toggle icons
         * {@type jQuery}
         */
        $gridListToggleIcons: null,

        /**
         * The current mode of this widget.
         */
        currentMode: null,

        /**
         * The text for the show all products button
         */
        showAllProducts: null,

        /**
         * Tabs setup
         */
        postCreate: function() {
            this.inherited(arguments);
            this.loadDom();
            this.renderGridListToggleTemplate();
            this.addShowAllProductsListener();
            this.addNumberOfResultsShownListener();
            this.setActiveMode(ViewMode.grid);
        },

        /**
         * Loads the needed DOM elements into the widget.
         */
        loadDom: function() {
            this.$domNode = $(this.domNode);

            // Note the data-parse for this attribute in the render template below is strange because we wanted
            // to reuse an existing SDL translatable data attribute
            this.showAllProducts = this.$domNode.data('section-heading-text');
        },

        /**
         * Sets the currently active mode for this widget. Will re-render the widget.
         *
         * @param mode The mode to set as active
         */
        setActiveMode: function(mode){
            // Save current mode
            this.currentMode = mode;

            // Set active icon
            this.$gridListToggleIcons.each($.proxy(function(i, element){
                var $currentElement = $(element);

                // Remove .active from all elements.
                $currentElement.removeClass('active');

                // Add it back for the current mode.
                if($currentElement.data('mode') === this.currentMode){
                    $currentElement.addClass('active');
                }
            }, this));
        },

        /**
         * Renders the template into the DOM.
         */
        renderGridListToggleTemplate: function(){
            // Render template
            this.$domNode.html(renderTemplate(gridListToggleTemplate, {
                showAllProducts: this.showAllProducts
            }));

            // Reload toggle icon DOM elements
            this.$gridListToggleIcons = $(GRID_TOGGLE_ICON_SELECTOR, this.$domNode);

            // Add click listener to the filters
            this.addToggleListener();
        },

        /**
         * Adds the click listener for grid/list toggle
         */
        addToggleListener: function(){
            this.$gridListToggleIcons.click($.proxy(function(event){
                // Get the new mode
                var $element = $(event.currentTarget);
                var selectedMode = $element.data('mode');

                // Don't run if the current mode was clicked.
                if(this.currentMode !== selectedMode) {
                    // Set the current mode
                    this.setActiveMode(ViewMode[selectedMode]);

                    // Publish the event so that other product filter widgets can update.
                    topic.publish(events.productFilter.gridListToggled, this.currentMode);
                }
            }, this));
        },

        /**
         * Subscribe to when the number of results displayed changes from our Products.js file.
         * When they do change, update our 'Showing X of XX Results' button, and hide our 'Show All Products'
         * button if all products are already being shown.
         */
        addNumberOfResultsShownListener: function() {
            this.own(topic.subscribe(events.productFilter.numberOfResultsChange, $.proxy(function(data){
                // Grab our translation for showing results, inject our result numbers, and render them in our html
                var resultsShown = dojoString.substitute(window.deluxeFilterTranslations.showingResults, {
                    numResultsShown: data.shownResults,
                    totalSearchResults: data.totalResults,
                    query: ''
                });
                $(NUMBER_OF_RESULTS_SHOWN_SELECTOR).html(resultsShown);

                // Toggle our show all products button based on whether or not all products are being shown already
                $(SHOW_ALL_PRODUCTS_SELECTOR, this.$domNode).toggle(data.shownResults < data.totalResults);
            }, this)));
        },

        /**
         * Publishes an event when the show all products button is clicked, so that the main
         * widget can render all products
         */
        addShowAllProductsListener: function() {
            $(SHOW_ALL_PRODUCTS_SELECTOR, this.$domNode).click($.proxy(function() {
                topic.publish(events.productFilter.showAllProducts);
            }, this));
        }
    });
});
},
