        /*
         * Sets the active parameter, adds the ACTIVE_CLASS and puts focus on the node
         * @private
         */
        _setActiveAttr: function (active) {
            this.active = active;

            if (active) {
                focus.focus(this.domNode);
            } // Sets "focused" property
            domClass.toggle(this.domNode, this.ACTIVE_CLASS, active);
        },

