            // Create typed allow and not_deny (shorthand for !deny)
            var allow = {
                dijit: createFilterFunction(this.DEACTIVATION_FILTER.dijit.allow, function (n, c) {
                    return n.declaredClass === c;
                }),
                html: createFilterFunction(this.DEACTIVATION_FILTER.html.allow, this.matchesSelector)
            };
            var notDeny = {
                dijit: createFilterFunction(this.DEACTIVATION_FILTER.dijit.deny, function (n, c) {
                    return n.declaredClass !== c;
                }),
                html: createFilterFunction(this.DEACTIVATION_FILTER.html.deny, this.matchesSelector, true)
            };

            // Now test the nodes
            return nodes.every(function (node) {
                var dijitNode = registry.byNode(node);
                if (typeof dijitNode === 'undefined') {
                    return allow.html(node) && notDeny.html(node);
                }
                else {
                    return allow.dijit(dijitNode) && notDeny.dijit(dijitNode);
                }
            });
