                var inputType = input.type.toLowerCase(),
                    inputValue  = (inputType === 'checkbox')        ?   !!input.checked
                                : (inputType === 'radio')           ?   (input.checked ? input.value : undefined)
                                : (inputType === 'select-multiple') ?   _.map(_.filter(input.options, function (opt) {
                                                                            return opt.selected;
                                                                        }), function (opt) {
                                                                            return opt.value;
                                                                        })
                                :                                       input.value;
