'vodori/util/string/startsWith':function(){
define([
    'module',
    'vodori/provide'
], function (module, provide) {
    return provide(module.id, function (str, subStr) {
        if (typeof str !== 'string' || typeof subStr !== 'string') {
            throw new Error((typeof str !== 'string' ? String(str) : String(subStr)) +
                ' is not a string');
        }
        return String(str).slice(0, subStr.length) === String(subStr);
    });
});

},
