            TEMPLATE_IDENTIFIER_CLASS: 'node-repeater-template',
            NODE_IDENTIFIER_CLASS: 'node-repeater-node',
            ADD_BUTTON_IDENTIFIER_CLASS: 'node-repeater-add',
            REMOVE_BUTTON_IDENTIFIER_CLASS: 'node-repeater-remove',
            ADD_POSITION_ATTRIBUTE: 'data-position',

            _foundTemplate: null,      // A child with a "_repeatableTemplate" class (destroyed at startup)
            _repeatableTemplate: null, // An unparsed clone of this._foundTemplate

            _insertionPointParent: null,
