                if (!this._foundTemplate) {
                    console.error('No repeatable template found', this.domNode);
                    return;
                }

                this._identifyInsertionPoint();

                // Clone it so its child widgets aren't parsed
                this._repeatableTemplate = lang.clone(this._foundTemplate);
                cleanup.destroy(this._foundTemplate);

                // Adjust the template's class
                domClass.remove(this._repeatableTemplate, this.TEMPLATE_IDENTIFIER_CLASS);
