'abbott/ui/am/aboutus/LocationsToggler':function(){
define([
    'vodori/provide', 'module',
    'lodash',
    'dijit/_Widget',
    'dojo/_base/lang', 'dojo/on', 'dojo/query', 'dojo/dom-class', 'dojo/dom-style',
    'abbott/util/_BehavioralAttachPoints', 'abbott/util/_BehavioralNestedWidgets'
], function(provide, module,
            _,
            _Widget,
            lang, on, query, domClass, domStyle,
            _BehavioralAttachPoints, _BehavioralNestedWidgets){
    return provide(module.id, [_Widget, _BehavioralAttachPoints, _BehavioralNestedWidgets],  {

        // ------------------------------------------ //
        //  NOTE: This was ported directly from ADD   //
        // ------------------------------------------ //

        locationsNav: null,
        locationsNavItems: null,

        allRegions: null,
        activeRegion: 0,

        _globalHeadquarters: null,

        /**
         *  Switch between the various Worldwide Locations
         */
        postCreate: function() {
            this.inherited(arguments);

            this._queries();

            _.forEach(this.locationsNavItem, function(item, index){
                // Loop through each nav item and connect to its clicks
                on(item, 'click', lang.hitch(this, '_toggleSection', item, index));
            }, this);
        },

        /**
         * Queries for getting the sections and the nav
         * @private
         */
        _queries: function(){
            this.locationsNav       = query('.locations-nav', this.domNode)[0];
            this.locationsNavItem   = query('ul li a', this.locationsNav);

            this.allRegions         = query('.location.region', this.domNode);
        },

        /**
         * Toggling a section includes changing the active nav item and showing the correct region
         * @param item - The nav item that is clicked
         * @param index - The index of said nav item. Lets us know which region to show
         * @private
         */
        _toggleSection: function(item, index){
            // Don't do anything if they click on the item that's already active
            if(index != this.activeRegion){
                // Remove 'active' from the previously active nav item
                domClass.remove(this.locationsNavItem[this.activeRegion], 'active');
                // Add 'active' class to the newly clicked item
                domClass.add(item, 'active');

                if(index === 0){
                    // The first nav item is 'All', so show all sections.
                    this._showAllRegions();
                    // Also, show the GlobalHeadquarters section
                    domClass.remove(this._globalHeadquarters, 'hidden');

                } else {
                    // Assuming regions are ordered the same as the nav, we can just show the index.
                    // Need to subtract one though since there are less regions than nav items (since there is no 'All' region)
                    this._showSpecificRegion(index - 1);

                    // We're only showing the GlobalHeadquarters block if they're viewing NorthAmerica or All locations
                    if (domClass.contains(item, 'north-am-link')){
                        domClass.remove(this._globalHeadquarters, 'hidden');
                    } else {
                        domClass.add(this._globalHeadquarters, 'hidden');
                    }
                }
                this.activeRegion = index;
            }
        },

        /**
         * Show all regions. Means they clicked on the 'All' nav item.
         * @private
         */
        _showAllRegions: function() {
            _.forEach(this.allRegions, function(region){
                domStyle.set(region, 'display', 'block');
            });
        },

        /**
         * Hide all regions and then show the newly selected region
         * @param index - The index of the region to show
         * @private
         */
        _showSpecificRegion: function(index) {
            _.forEach(this.allRegions, function(region){
                domStyle.set(region, 'display', 'none');
            });
            domStyle.set(this.allRegions[index], 'display', 'block');
        }

    });
});
},
