'abbott/pandx/PanDxUtil':function(){
define([
    'module',
    'vodori/provide',
    'dojo/query'
], function(
    module,
    provide,
    query
) {

    var URI_LOCALE_PANDX_US_ENGLISH = '/us/en/',
        MARKET_NAME_US                            = 'U.S.',
        MARKET_NAME_OUS                         = 'OUS',
        MARKET_ID_US                                  = 1,
        MARKET_ID_OUS                                  = 2,
        MOLECULAR_CATALOG_PRODUCT_META_TAG_KEY = 'molecular-catalog-productId',
        MOLECULAR_CATALOG_PRODUCT_FAMILY_META_TAG_KEY = 'molecular-catalog-productFamilyId';
    var CONTENT_ATTRIBUTE = "content";
    var CONTENT_TYPE_JSON = "application/json";
    var CSRF_SELECTOR = "meta[name='_csrf']";
    var CSRF_HEADER_SELECTOR = "meta[name='_csrf_header']";

    return provide(module.id, {

        /**
         * Determines the market for the current page (either US or OUS).
         *
         * @returns {string} US if the current page is for the US market, OUS if it's an international page
         */
        getCurrentMarket: function() {
            var location = window.location;

            return location.pathname.indexOf(URI_LOCALE_PANDX_US_ENGLISH) > -1 ? MARKET_NAME_US : MARKET_NAME_OUS;
        },

        /**
         * Determines the market id for the current page (1 = US, 2 = OUS)
         *
         * @returns {number}
         */
        getCurrentMarketId: function () {
            var location = window.location;

            return location.pathname.indexOf(URI_LOCALE_PANDX_US_ENGLISH) > -1 ? MARKET_ID_US : MARKET_ID_OUS;
        },

        /**
         * Determines if the current request is for the US market
         *
         * @returns {boolean}
         */
        isUSMarket: function() {
            return this.getCurrentMarket() === MARKET_NAME_US;
        },

        /**
         * Get the value of a meta tag with the given key
         *
         * <meta data-lorem="ipsum" />
         * PanDxUtil.getMetaTagData('lorem') // returns 'ipsum'
         *
         * @param key
         * @returns {*}
         */
        getMetaTagData: function(key) {
            var metaTag = query('meta[data-' + key + ']', document.head)[0];
            if (metaTag) {
                return metaTag.getAttribute('data-' + key );
            } else {
                return null;
            }
        },

        /**
         * @returns product id of the current molecular catalog product page
         */
        getCatalogProductId: function () {
            return this.getMetaTagData(MOLECULAR_CATALOG_PRODUCT_META_TAG_KEY);
        },

        /**
         * @returns product family id of the current molecular catalog product page
         */
        getCatalogProductFamilyId: function () {
            return this.getMetaTagData(MOLECULAR_CATALOG_PRODUCT_FAMILY_META_TAG_KEY);
        },

        /**
         * Constructs headers for a POST request. Gets our CSRF headers and adds a JSON content type header.
         */
        getPostHeaders: function() {
            // Grab the CSRF token information
            var csrfToken = $(CSRF_SELECTOR).attr(CONTENT_ATTRIBUTE);
            var csrfHeader = $(CSRF_HEADER_SELECTOR).attr(CONTENT_ATTRIBUTE);

            // Build our headers
            var headers = {
                'Content-Type': CONTENT_TYPE_JSON
            };
            if (csrfToken && csrfHeader) {
                headers[csrfHeader] = csrfToken;
            }

            return headers
        },

        /**
         * Extracts error text from our error object.
         *
         * @param error the error object to extract error text from
         */
        extractErrorTextFromError: function(error) {
            // Extract our error text from the error response
            // If the error has a field errors object (from our error handler), grab that, otherwise just grab the response JSON
            if((error.responseJSON || {}).fieldErrors) {
                return this._constructFieldErrorMessage(error.responseJSON.fieldErrors);
            } else if(error.responseJSON) {
                return error.responseJSON;
            } else {
                return null;
            }
        },

        /**
         * Constructs the BE field error message to display to the user
         * @param fieldErrors
         * @returns {string}
         * @private
         */
        _constructFieldErrorMessage: function(fieldErrors) {
            var errorDisplayText = '';
            $.each(fieldErrors, $.proxy(function(i, fieldError) {
                errorDisplayText += fieldError.code + '</br>';
            }, this));
            return errorDisplayText;
        }
    });
});
},
