                onValidate: function() {
                    // Update classes on wrapper element so that error message and label display correctly
                    $('.has-error').each(function(i, element) {
                        $(element).addClass('form-error');
                    });

                    $('.has-success').each(function(i, element) {
                        var $element = $(element);
                        $element.removeClass('form-error');

                        // Remove errors from selects
                        if ($element.is('select')) {
                            var selectWrapper = $element.parent('.select-wrapper');
                            selectWrapper.removeClass('form-error');
                            $('.error-message', selectWrapper).remove();
                        }
                    });
                },
