'url:abbott/ui/gateway/template/GatewayModalDialog.html':"<div class=\"modalContent locale-leave-modal-content\">\n    <h5>\n        {{header}}\n    </h5>\n\n    <div class=\"country-selector-wrapper\">\n        <div class=\"country-selector-row\">\n            {{#countries}}\n            <a class=\"country-selector-item\" href=\"{{ uri }}\">\n                <img class=\"gateway-icon\" src=\"{{ imgUri }}\">\n                <p class=\"country-name\">{{ name }}</p>\n                <p class=\"country-language\">({{ language }})</p>\n            </a>\n            {{/countries}}\n        </div>\n    </div>\n\n    <p>\n        {{description}}\n    </p>\n    <div class=\"modal-footer-bar gateway--disclaimer\">\n        {{{disclaimer}}}\n    </div>\n</div>",
