            var t = this;

            t.editor = ed;

            ed.onBeforeSetContent.add(function (ed, o) {
                if (o.set) {
                    o.content = t._doReplacement(ed, o);
                }
            });

            ed.onPostProcess.add(function (ed, o) {
                if (o.get) {
                    o.content = t._doReplacement(ed, o);
                }
            });

            if (this.inherited) {
                this.inherited("init", arguments);
            }

