            if (!regexConfig) {
                return h;
            } // Don't replace anything if we're not configured to do so

            h = h.replace(/\r|\n|\r\n/g, "");

            var direction =     (o.get) ?   "fromTinyIntoPage"
                            :   (o.set) ?   "fromPageIntoTiny"
                            :               null;
