    'module',
    'vodori/provide',
    'lodash',
    'dojo/_base/lang', 'dojo/topic', 'dojo/on', 'dojo/json', 'dojo/query',
    'dojo/dom-style', 'dojo/dom-construct', 'dojo/dom-attr',
    'dojo/has', 'dojo/parser',
    'dijit/_WidgetBase',
